{
  "pages": [
    {
      "title": "Title & Effective Date",
      "content": {
        "standard_number": "FAS 4.DM",
        "title": "Accounting for Diminishing Musharaka in Real Estate Funds",
        "example_title": "Accounting for Innovative Digital Assets",
        "effective_date": "01 January 2025 (Gregorian) / 20 Jumada II 1446 (Hijri)",
        "issued_by": "AI-Driven Standard Enhancement System (Conceptual)"
      }
    },
    {
      "title": "Objective, Scope & Key Principles",
      "content": {
        "objective": [
          "To provide specific accounting guidance for diminishing Musharaka (shirkah al-\u02bfaqd) structures used in real estate funds, addressing the nuances of equity reduction, partner contributions/exits, and asset valuation.",
          "This update clarifies the application of FAS 4 principles in the context of real estate funds, ensuring Shari'ah compliance and fair value accounting."
        ],
        "scope": {
          "applicable_transactions_entities": "This standard applies to Islamic financial institutions and real estate funds utilizing diminishing Musharaka (shirkah al-\u02bfaqd) contracts.",
          "exclusions": "This standard does not cover general Musharaka contracts that are not diminishing or those that do not involve real estate funds, or other forms of Islamic partnerships."
        },
        "key_shariah_principles": [
          "Fairness and Transparency (\u02bfAdl wa Ihsan): All transactions must be conducted with fairness and transparency, ensuring no party is unjustly enriched or disadvantaged.",
          "Prohibition of Riba (Interest): All aspects of the Musharaka, including partner contributions, exits, and profit distribution, must be free from interest-based elements.",
          "Profit and Loss Sharing: Profits and losses must be shared among partners based on pre-agreed ratios proportionate to their equity share.",
          "Independent Valuation: To ensure fair value, independent valuation methods must be used for asset contributions and exits."
        ]
      }
    },
    {
      "title": "Definitions",
      "content": {
        "definitions": [
          {
            "term": "Diminishing Musharaka (shirkah al-\u02bfaqd)",
            "definition": "A partnership agreement where one partner gradually reduces their equity share, leading to the other partner eventually owning the entire asset."
          },
          {
            "term": "Real Estate Fund",
            "definition": "A fund that invests primarily in real estate assets, structured according to Shari'ah principles."
          },
          {
            "term": "Independent Valuer",
            "definition": "A qualified and impartial professional who assesses the fair market value of assets."
          },
          {
            "term": "Fair Market Value",
            "definition": "The price at which an asset would change hands between a willing buyer and a willing seller, both having reasonable knowledge of the relevant facts."
          }
        ]
      }
    },
    {
      "title": "Recognition and Initial Measurement",
      "content": {
        "recognition": {
          "general_criteria": "A partner's contribution to the Musharaka should be recognized as an asset in the fund's financial statements when the fund obtains control of the asset and it is probable that future economic benefits will flow to the fund.",
          "specific_points": "Cash contributions are recognized at their nominal value. In-kind contributions (e.g., real estate) are recognized at fair market value at the date of contribution, as determined by an independent valuation."
        },
        "initial_measurement": {
          "general_principle": "Assets contributed to the Musharaka should be initially measured at fair value.",
          "specific_guidance": "For cash contributions, the initial measurement is the amount of cash contributed. For in-kind contributions, the initial measurement is the fair value as determined by an independent valuer, considering any directly attributable costs."
        }
      }
    },
    {
      "title": "Subsequent Measurement & De-recognition",
      "content": {
        "subsequent_measurement": {
          "measurement_basis": "Real estate assets held within the Diminishing Musharaka should be subsequently measured in accordance with the fund\u2019s accounting policy and relevant standards. The fair value model should be used where appropriate.",
          "changes_in_value": "Changes in the fair value of the real estate assets should be recognized in the period in which they arise.",
          "profit_loss_recognition": "Profits or losses arising from the revaluation of real estate assets upon a partner's entry or exit shall be allocated to the partners based on their respective equity shares at the time of the valuation. Any mechanism to avoid this is not allowed."
        },
        "de_recognition": {
          "criteria": "A partner's equity share is derecognized when the partner exits the Musharaka, or when the partnership agreement is terminated.",
          "accounting": "Any gain or loss on de-recognition is calculated as the difference between the proceeds received by the exiting partner and the carrying amount of their equity share. This gain or loss is recognized in profit or loss."
        }
      }
    },
    {
      "title": "Disclosure Requirements",
      "content": {
        "qualitative_disclosures": [
          "Description of the nature of the Diminishing Musharaka arrangements.",
          "Key accounting policies adopted for measuring real estate assets.",
          "Information about the independent valuation process and the qualifications of the independent valuers.",
          "Explanation of how the Diminishing Musharaka complies with Shari'ah principles.",
          "The mechanism for the reduction of partner's equity share."
        ],
        "quantitative_disclosures": [
          "Breakdown of the carrying amounts of real estate assets held under Diminishing Musharaka.",
          "Reconciliation of movements in the carrying amounts of partners' equity shares.",
          "Income and expenses recognized related to the Diminishing Musharaka.",
          "Maturity analysis of the Diminishing Musharaka arrangements.",
          "Information on any impaired real estate assets and provisions related to the Diminishing Musharaka."
        ]
      }
    },
    {
      "title": "Basis for Conclusions (Summary)",
      "content": {
        "rationale": "This amendment to FAS 4 provides specific guidance on the accounting for Diminishing Musharaka (shirkah al-\u02bfaqd) in real estate funds. This guidance is needed because the existing standard did not fully address the unique aspects of these arrangements, particularly concerning partner contributions/exits and asset valuation.",
        "shariah_compliance": "The accounting treatments prescribed in this standard ensure alignment with Shari'ah principles by requiring fair valuation, equitable profit and loss sharing, and transparency in all transactions. The standard relies on AAOIFI standards and fatwas to ensure compliance.",
        "addressing_gaps": "This standard addresses the specific user context by providing detailed guidance on the accounting treatment for equity reduction, partner contributions/exits, and asset valuation in the context of diminishing Musharaka real estate funds. This improves the clarity and consistency of financial reporting for these transactions."
      }
    }
  ]
}
