{
  "pages": [
    {
      "title": "Title & Effective Date",
      "content": {
        "standard_number": "FAS 4-R1",
        "title": "Accounting for Diminishing Musharaka in Real Estate Funds",
        "example_title": "Accounting for Innovative Digital Assets",
        "effective_date": "01 January 2025 (Gregorian) / 19 Jumada II 1446 (Hijri)",
        "issued_by": "AI-Driven Standard Enhancement System (Conceptual)"
      }
    },
    {
      "title": "Objective, Scope & Key Principles",
      "content": {
        "objective": [
          "To provide specific accounting guidance for Diminishing Musharaka arrangements within Real Estate Funds, addressing the unique characteristics of these structures.",
          "This standard clarifies the application of Shirkah al-\u02bfAqd principles, profit/loss distribution, asset valuation, partner exits, and disclosure requirements for these funds."
        ],
        "scope": {
          "applicable_transactions_entities": "This standard applies to Islamic Financial Institutions and Real Estate Funds utilizing Diminishing Musharaka contracts structured under Shirkah al-\u02bfAqd principles.",
          "exclusions": "This standard does not cover general Shirkah structures outside the context of Real Estate Funds or other types of Musharaka arrangements."
        },
        "key_shariah_principles": [
          "Shirkah al-\u02bfAqd: All partners must contribute either in cash or in-kind assets, with contributions specified and agreed upon at inception.",
          "Profit and Loss Sharing: Profit distribution is based on a pre-agreed ratio, while losses are borne in proportion to capital contribution.",
          "Fair Valuation: Real estate assets must be valued at fair market value by independent appraisers."
        ]
      }
    },
    {
      "title": "Definitions",
      "content": {
        "definitions": [
          {
            "term": "Diminishing Musharaka",
            "definition": "A Shirkah (partnership) where one partner gradually transfers their ownership share to the other partner(s) over time, typically through periodic payments."
          },
          {
            "term": "Shirkah al-\u02bfAqd",
            "definition": "A contractual partnership where two or more parties agree to contribute capital and share in the profits and losses of a business venture."
          },
          {
            "term": "Fair Market Value",
            "definition": "The price that would be received to sell an asset or paid to transfer a liability in an orderly transaction between market participants at the measurement date."
          },
          {
            "term": "Capital Contribution",
            "definition": "The amount of cash or the fair value of in-kind assets contributed by each partner to the Musharaka fund."
          }
        ]
      }
    },
    {
      "title": "Recognition and Initial Measurement",
      "content": {
        "recognition": {
          "general_criteria": "Assets and liabilities arising from Diminishing Musharaka in Real Estate Funds should be recognized when it is probable that future economic benefits will flow to or from the entity and the cost or value of the item can be measured reliably.",
          "specific_points": "Partner contributions, whether in cash or in-kind, are recognized at fair value at the inception of the Musharaka agreement. The nature and valuation of these contributions should be clearly disclosed."
        },
        "initial_measurement": {
          "general_principle": "Assets and liabilities should be initially measured at fair value.",
          "specific_guidance": "The fair value of in-kind contributions should be determined based on independent valuation or appraisal. Cash contributions are measured at the amount contributed."
        }
      }
    },
    {
      "title": "Subsequent Measurement & De-recognition",
      "content": {
        "subsequent_measurement": {
          "measurement_basis": "Real estate assets within a Diminishing Musharaka fund shall be valued at least annually, or more frequently if significant market fluctuations occur, using independent, qualified appraisers based on fair market value.",
          "changes_in_value": "Changes in the fair value of real estate assets are recognized in profit or loss. Impairment should be recognized immediately if the fair value falls below the carrying value.",
          "profit_loss_recognition": "Profit distribution shall be recognized based on the agreed profit-sharing ratio, determined proportionally to the capital contributions of each partner at the time of profit calculation. Losses shall be borne by each partner strictly in proportion to their capital contribution at the time the loss is incurred."
        },
        "de_recognition": {
          "criteria": "An asset is derecognized when the entity loses control of it. For partner exits, the unit is derecognized when the exiting partner relinquishes their rights and obligations associated with the unit.",
          "accounting": "Any gain or loss arising on de-recognition, representing the difference between the carrying amount and the consideration received, is recognized in profit or loss and allocated to the exiting partner's capital account."
        }
      }
    },
    {
      "title": "Disclosure Requirements",
      "content": {
        "qualitative_disclosures": [
          "Description of the Diminishing Musharaka arrangement, including the nature of the underlying real estate assets and the profit-sharing mechanism.",
          "Accounting policies adopted for the measurement of real estate assets and the recognition of profit and loss.",
          "Information about risk management practices related to the real estate fund, including market risk, liquidity risk, and operational risk.",
          "Confirmation of Shari'ah compliance by the Shariah Supervisory Board."
        ],
        "quantitative_disclosures": [
          "Breakdown of the carrying amounts of real estate assets held under the Diminishing Musharaka arrangement.",
          "Reconciliation of movements in the carrying amounts of real estate assets, including additions, disposals, and revaluations.",
          "Income and expenses recognized from the Diminishing Musharaka arrangement.",
          "Information on partner exits and unit redemptions, including the method for determining exit prices and the treatment of gains or losses.",
          "Disclosure of profit-sharing ratios and their justification."
        ]
      }
    },
    {
      "title": "Basis for Conclusions (Summary)",
      "content": {
        "rationale": "This standard provides specific guidance for Diminishing Musharaka arrangements in Real Estate Funds, addressing gaps in FAS 4 regarding Shirkah al-\u02bfAqd principles, profit/loss distribution, asset valuation, and partner exits. These provisions promote consistency and comparability in financial reporting.",
        "shariah_compliance": "The standard ensures Shari'ah compliance by adhering to the principles of Shirkah al-\u02bfAqd, profit and loss sharing, and fair valuation as outlined in AAOIFI standards.",
        "addressing_gaps": "This standard addresses the specific user context by providing detailed guidelines for applying Shirkah principles to real estate funds, clarifying profit/loss distribution, providing specific real estate valuation rules, establishing procedures for fair partner exits, and enhancing disclosure requirements."
      }
    }
  ]
}
